SELECT a.Course_code, SUM(CASE WHEN a.Present = 'present' THEN 1 ELSE 0 END) AS Total_present, SUM(CASE WHEN a.Present = 'absent' THEN 1 ELSE 0 END) AS Total_absent, ROUND((SUM(CASE WHEN a.Present = 'present' THEN 1 ELSE 0 END) * 100) / (SUM(CASE WHEN a.Present = 'present' THEN 1 ELSE 0 END) + SUM(CASE WHEN a.Present = 'absent' THEN 1 ELSE 0 END)), 2) AS Attendance_percent FROM ATTENDANCE a INNER JOIN COURSE c ON a.Course_code = c.Course_code WHERE a.Sid = 'DE2148' AND a.Course_code = c.Course_code GROUP BY a.Course_code, c.Course_code;